//get accessories doc from db
db.collection("/kora/product/ui design/").onSnapshot((snapshot) => {
    fetchTrending(snapshot.docs);
});

// --------------------fetch accessories from backend --------------------
const exploreContainer = document.querySelector(".koraExplore");

const fetchTrending = (data) => {
    let html = "";
    data.forEach((doc) => {
        const product = doc.data();
        const li = `

    <div class="col-xl-3 col-lg-4 col-sm-6">
       <div class="card card-s3">
          <div class="card-image">
            <a class="fancybox" href="${product.img}" data-fancybox-group="gallery" title="${product.name}">
              <img src="${product.img}" class="card-img" alt="Kora"/>
            </a>
          </div>
          <div class="card-body px-0 pb-0">
             <h5 class="card-title text-truncate mb-0">
                <a>${product.name}</a>
            </h5>
             <div class="card-author mb-1 d-flex align-items-center">
                <span class="me-1 card-author-by">By</span>
                <div class="custom-tooltip-wrap">
                   <a href="javascript:void(0)" class="custom-tooltip author-link">${product.author}</a>
                   <!-- end dropdown-menu -->
                </div>
             </div>
          </div>
       </div>
    </div>
    
            `;

        html += li;
    });

    exploreContainer.innerHTML = html;
};

// --------------------add to cart from frontend--------------------
function addToCart(name, price, image) {
    var user = firebase.auth().currentUser;

    if (user) {
        // User is signed in.
    } else {
        // No user is signed in.
        M.toast({ html: "Please Login or Signup" });
    }

    db.doc(`users/${user.email}`)
        .collection("usercart")
        .doc(`${name}`)
        .get()
        .then((doc) => {
            console.log(doc.exists);
            if (doc.exists) {
                M.toast({ html: "Item already added to your cart" });
            } else {
                db.doc(`users/${user.email}`)
                    .collection(`usercart`)
                    .doc(`${name}`)
                    .set({
                        useruid: user.uid,
                        name: name,
                        price: Number(price),
                        image: image,
                    })
                    .then(() => {
                        M.toast({ html: "Item added to your cart" });
                    });
            }
        });
}
